import React, { useEffect, useState } from 'react';
import { Transaction, Category } from '../types';
import { apiService } from '../services/api';

interface TransactionTableProps {
  transactions: Transaction[];
  onTransactionUpdate: () => void;
}

const TransactionTable: React.FC<TransactionTableProps> = ({ transactions, onTransactionUpdate }) => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [editingTransaction, setEditingTransaction] = useState<number | null>(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const data = await apiService.getCategories();
        setCategories(data);
      } catch (error) {
        console.error('Failed to fetch categories:', error);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryChange = async (transactionId: number, newCategoryId: string) => {
    try {
      await apiService.updateTransactionCategory(transactionId, newCategoryId || null);
      setEditingTransaction(null);
      onTransactionUpdate();
    } catch (error) {
      console.error('Failed to update category:', error);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' });
  };

  const formatCurrency = (amount: number) => {
    const sign = amount < 0 ? '-' : '';
    return `${sign}$${Math.abs(amount).toFixed(2)}`;
  };

  return (
    <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4 text-gray-900 dark:text-gray-100">Recent Transactions</h2>

      {transactions.length === 0 ? (
        <p className="text-gray-500 dark:text-gray-400">No transactions found...</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full table-auto">
            <thead className="bg-gray-50 dark:bg-gray-700">
              <tr>
                <th className="px-4 py-2 text-left text-sm font-medium text-gray-700 dark:text-gray-300">Date</th>
                <th className="px-4 py-2 text-left text-sm font-medium text-gray-700 dark:text-gray-300">Description</th>
                <th className="px-4 py-2 text-right text-sm font-medium text-gray-700 dark:text-gray-300">Amount</th>
                <th className="px-4 py-2 text-left text-sm font-medium text-gray-700 dark:text-gray-300">Category</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200 dark:divide-gray-600">
              {transactions.map((tx) => (
                <tr key={tx.id} className="hover:bg-gray-50 dark:hover:bg-gray-700">
                  <td className="px-4 py-2 text-sm text-gray-900 dark:text-gray-100">{formatDate(tx.date)}</td>
                  <td className="px-4 py-2 text-sm text-gray-700 dark:text-gray-300">{tx.description}</td>
                  <td
                    className={`px-4 py-2 text-sm text-right font-medium ${
                      tx.amount < 0 ? 'text-red-600 dark:text-red-400' : 'text-green-600 dark:text-green-400'
                    }`}
                  >
                    {formatCurrency(tx.amount)}
                  </td>
                  <td className="px-4 py-2 text-sm text-gray-700 dark:text-gray-300">
                    {editingTransaction === tx.id ? (
                      <select
                        value={tx.category_id || ''}
                        onChange={(e) => handleCategoryChange(tx.id, e.target.value)}
                        className="border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 rounded px-2 py-1 text-sm"
                      >
                        <option value="">Uncategorized</option>
                        {categories.map((cat) => (
                          <option key={cat.id} value={cat.id}>
                            {cat.name}
                          </option>
                        ))}
                      </select>
                    ) : (
                      <button
                        onClick={() => setEditingTransaction(tx.id)}
                        className="text-blue-600 dark:text-blue-400 hover:underline"
                      >
                        {tx.category || 'Uncategorized'}
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default TransactionTable;
